<script type="text/javascript">
    $(document).ready(function() {
        $('#link-sub-button').click(function(event) {
            event.preventDefault();
            window.location = "/authentication/link-subscription";
        });

        $("#signin-form").submit(function(event) {
            $("#signin-form-button").prop('disabled', true);
            var url = "/authentication";
            $.ajax({
                type: "POST",
                url: url,
                data: $("#signin-form").serialize(),
                success: function(data) {
                    if (!data.error) {
                        $('.modal-state:checked').prop('checked', false).change();
                        $('.sign-in-name').html('Welcome, '+data.name+' <a href="/authentication/sign_out" ><small>(Sign out)</small></a>');
                        $("#signin-form-button").prop('disabled', false);
                        if (typeof check_article_auth == 'function') {
                            check_article_auth();
                        }
                    } else {
                        $("#signin-form-button").prop('disabled', false);
                        $('#signin_error').text(data.error);
                    }
                },
                error: function(data) {
                    $("#signin-form-button").prop('disabled', false);
                    var response = jQuery.parseJSON( data.responseText );
                    $('#signin_error').text("There was an error signing in. Please try again.");
                },
            });
            event.preventDefault();
        });

        var name_url = "/authentication/get-name";
        $.ajax({
            type: "GET",
            url: name_url,
            success: function(data) {
                if (!data.error) {
                    $('.sign-in-name').html('Welcome, '+data.name+' <a href="/authentication/sign_out" ><small>(Sign out)</small></a>');
                }
            },
        });
    });
</script>
